document.addEventListener("DOMContentLoaded",function(){let changeMapInterval=setInterval(()=>{changeMap(document.querySelector(".contact-map-scroller div:not(.active)"))},5000);for(const scroller of document.querySelectorAll(".contact-map-scroller>div")){scroller.addEventListener("click",function(){clearInterval(changeMapInterval);changeMap(this);})}})
function changeMap(element){document.querySelector(".contact-map-scroller .active").classList.remove("active");element.classList.add("active");document.querySelector(".contact-map .active").classList.remove("active");document.querySelector(`#${element.dataset.map}`).classList.add("active");document.querySelector(".contact-map-details .active").classList.remove("active");document.querySelector(`#${element.dataset.map}Details`).classList.add("active");}