document.addEventListener("DOMContentLoaded",function(){for(const tab of document.querySelectorAll(".index-service-select-tab")){tab.addEventListener('click',function(event){document.querySelector(".index-service-select-tab.active").classList.remove("active");this.classList+=" active";document.querySelector(".index-service-highlight").style.left=`${(parseInt(this.dataset.order)-1)*20}%`;document.querySelector(".index-service-content.active").classList.remove("active");document.querySelector(`#${this.dataset.tab}`).classList+=" active";})}
document.querySelector(".bdnaCircles").addEventListener("click",function(){let pos=getPosition(document.querySelector(".index-focuses"));window.scrollTo({top:pos.y,behavior:"smooth"})})
function getPosition(element){var xPosition=0;var yPosition=0;while(element){xPosition+=(element.offsetLeft-element.scrollLeft+element.clientLeft);yPosition+=(element.offsetTop-element.scrollTop+element.clientTop);element=element.offsetParent;}
return{x:xPosition,y:yPosition};}})